import siotimport timefrom xugu import Pin # 从 xugu 库中导入 Pin类p = Pin("A0", Pin.ANALOG) # 初始化 A0 引脚,设置为输入模式SERVER = "192.168.0.101"        #MQTT服务器IPCLIENT_ID = ""              #在SIoT上，CLIENT_ID可以留空IOT_pubTopic  = 'DIY/TEST01'   #“topic”为“项目名称/设备名称”IOT_UserName ='scope'        #用户名IOT_PassWord ='scope'     #密码siot.init(CLIENT_ID, SERVER, user=IOT_UserName, password=IOT_PassWord) def sub_cb(client, userdata, msg):  print("\nTopic:" + str(msg.topic) + " Message:" + str(msg.payload))    siot.connect()siot.set_callback(sub_cb)siot.getsubscribe(IOT_pubTopic)siot.loop()while True:  TDS = p.read_analog() #读取 A0 引脚的模拟量  siot.publish(IOT_pubTopic, "%d"%TDS)  time.sleep(1)